import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import LinearProgress from '@material-ui/core/LinearProgress';
import Box from "@material-ui/core/Box";
import t from '../utils/translation';
import { Typography } from '@material-ui/core';
import Link from '../src/Link';

const useStyles = makeStyles(theme => ({
  container: {
    display: 'grid',
    gridTemplateColumns: 'repeat(12, 1fr)',
    gridGap: theme.spacing(3),
  },
  paper: {
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    whiteSpace: 'nowrap',
    marginBottom: theme.spacing(1),
  },
  divider: {
    margin: theme.spacing(2, 0),
  },
  icon: {
    fontSize: '60px'
  }
}));

export default () => {
  const [loading, setLoading] = useState(false);
  const classes = useStyles();

  const ButtonSelector = ({ href, text, icon }) => (
    <Link href={href} color="secondary">
      <Paper className={classes.paper} onClick={() => {
        localStorage.setItem(`${text}`, text);
      }}>
        <i className={`${classes.icon} ${icon}`}></i>
        <Typography variant="caption" display="block" gutterBottom>
          {t(text)}
        </Typography>
      </Paper>
    </Link>
  )
  return (
    <>
      {loading && <LinearProgress />}
      <Container maxWidth="lg">
        {!loading &&
          <>
            <Box my={2}>
              <Typography variant="h6" component="h1">
                {t('type.of.food')}
              </Typography>
            </Box>
            <Box my={1}>
              <Paper className={classes.paper}>
                <Grid container spacing={3}>
                  <Grid item md={4} xs={6}>
                    <ButtonSelector href='/recipes' text='vegetarian' icon='fas fa-leaf' />
                  </Grid>
                  <Grid item md={4} xs={6}>
                    <ButtonSelector href='/recipes' text='vegan' icon='fas fa-seedling' />
                  </Grid>
                  <Grid item md={4} xs={6}>
                    <ButtonSelector href='/recipes' text='indifferent' icon='fas fa-utensils' />
                  </Grid>
                </Grid>
              </Paper>
            </Box>
          </>
        }
      </Container>
    </>
  );
}
