const language = 'es';
const TEXTS = [
	{
		key: 'vegan',
		es: 'Vegano',
		en: 'Vegan'
	},
	{
		key: 'vegetarian',
		es: 'Vegetariano',
		en: 'Vegetarian'
	},
	{
		key: 'indifferent',
		es: 'Indiferente',
		en: 'Indifferent'
	},
	{
		key: 'type.of.food',
		es: 'Seleccione tipo de comida',
		en: 'Select type of food'
	},
	{
		key: 'recipes',
		es: 'Recetas',
		en: 'Recipes'
	},
	{
		key: 'vegetables.selector',
		es: 'Seleccione sus vegetales',
		en: 'Select your vegetables'
	}
];

export default (key) => {
	try {
		return TEXTS.find(t => t.key === key)[language]
	} catch (error) {
		console.log(`Error find the key: ${key}`);
		return key;
	}

};
