import React, { useState } from 'react';
import { useRouter } from 'next/router'
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import LinearProgress from '@material-ui/core/LinearProgress';
import Box from "@material-ui/core/Box";
import t from '../../utils/translation';
import { Typography } from '@material-ui/core';
import { getRecipesByType } from "../../resources/recipes";

const useStyles = makeStyles(theme => ({
	container: {
		display: 'grid',
		gridTemplateColumns: 'repeat(12, 1fr)',
		gridGap: theme.spacing(3),
	},
	paper: {
		padding: theme.spacing(1),
		textAlign: 'center',
		color: theme.palette.text.secondary,
		whiteSpace: 'nowrap',
		marginBottom: theme.spacing(1),
	},
	divider: {
		margin: theme.spacing(2, 0),
	},
	icon: {
		fontSize: '60px'
	}
}));

export default () => {
	const [loading, setLoading] = useState(true);
	const [recipes, setRecipes] = useState(true);
	const classes = useStyles();

	const loadRecipes = async (typeOfFood) => {
		try {
			const response = await getRecipesByType(typeOfFood);
			setLoading(false);
			setRecipes(response);
		} catch (error) {
			console.log("Error", error);
			setLoading(false);
		}
	}

	// De forma similar a componentDidMount y componentDidUpdate
	useEffect(() => {
		const router = useRouter()
		const { typeOfFood } = router.query;
		loadRecipes(typeOfFood);
	});

	return (
		<>
			{loading && <LinearProgress />}
			<Container maxWidth="lg">
				{!loading &&
					<>
						<Box my={2}>
							<Typography variant="h6" component="h1">
								{t('recipes')}
							</Typography>
						</Box>
						<Box my={1}>
							<Paper className={classes.paper}>
								{recipes.map(r => (
									<div></div>
								))}
							</Paper>
						</Box>
					</>
				}
			</Container>
		</>
	);
}
