const language = 'es';
const TEXTS = [
    {
        key: 'vegan',
        es: 'Vegano',
        en: 'Vegan'
    },
    {
        key: 'vegetarian',
        es: 'Vegetariano',
        en: 'Vegetarian'
    }
];

export default (key) => TEXTS.find(t => t.key === key)[language];
