const language = 'es';
const TEXTS = [
    {
        key: 'vegan',
        es: 'Vegano',
        en: 'Vegan'
    },
    {
        key: 'vegetarian',
        es: 'Vegetariano',
        en: 'Vegetarian'
    },
    {
        key: 'indifferent',
        es: 'Indiferente',
        en: 'Indifferent'
    }
];

export default (key) => {
    try {
        return TEXTS.find(t => t.key === key)[language]
    } catch (error) {
        console.log(`Error find the key: ${key}`);
        return key;
    }

};
